import React from 'react';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import NativeSelect from "@material-ui/core/NativeSelect";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),

  },
  row:{
    display:'flex',
    flexDirection:'row',
    justifyContent:'space-around'
  }
}));

export default function Business(props) {
  const classes = useStyles();
  return (
    <React.Fragment>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <TextField required id="business_name" onChange={(e)=> props.setBusName(e.target.value)}
                     label="Business name" fullWidth autoComplete="cc-name" />
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField
            required
            id="business_name"
            label="Business name"
            fullWidth
            autoComplete="business_name"
          />
        </Grid>
        <Grid item xs={12}>
          <label style={{fontWeight:'bold'}}>Location :</label>

          <Grid item xs={12}>
            <TextField
                required
                id="town"
                name="town"
                label="Town"
                fullWidth
                // onChange={(e)=> props.setTown(e.target.value)}
                autoComplete="contact_number"
            />
          </Grid>
          <Grid item xs={12} className={classes.row}>

          <Grid item xs={12} md={6}>
              <NativeSelect
                  className={classes.selectEmpty}
                  name="age"
                  inputProps={{ 'aria-label': 'district' }}
                  fullWidth
              >
                <option value="">
                  District
                </option>
                <option value={10}>Ten</option>
                <option value={20}>Twenty</option>
                <option value={30}>Thirty</option>
              </NativeSelect>
          </Grid>

          <Grid item xs={12} md={6}>
              <NativeSelect
                  className={classes.selectEmpty}
                  name="region"
                  inputProps={{ 'aria-label': 'region' }}
                  fullWidth
              >
                <option value="">
                  Region
                </option>
                <option value={10}>Ten</option>
                <option value={20}>Twenty</option>
                <option value={30}>Thirty</option>
              </NativeSelect>
          </Grid>
          </Grid>

        </Grid>
      </Grid>
    </React.Fragment>
  );
}
