import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import Checkbox from "@material-ui/core/Checkbox";
import TextField from "@material-ui/core/TextField";
import { Get } from "../util/transport";

export default function TrainForm(props) {
  const [trained, setTrained] = useState(false);
  const [wantTrain, setWantTrain] = useState(false);
  const [regions, setRegions] = useState([]);

  const getAllRegions = async () => {
    Get('region/wapp/regions');
  };
  useEffect(() => {}, []);

  return (
    <React.Fragment>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <div style={{}}>
            <label style={{ fontWeight: "bold" }}>
              Have you trained apprentices before?
            </label>
            <Checkbox
              checked={trained}
              onChange={(e) => setTrained(e.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            yes
            <Checkbox
              checked={!trained ? true : false}
              onChange={(event) => setTrained(!event.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            No
          </div>
        </Grid>
        {!trained ? (
          <Grid item xs={12}>
            <div style={{}}>
              <label style={{ fontWeight: "bold" }}>
                Would you want to receive support from NBSSI?
              </label>
              <Checkbox
                checked={wantTrain}
                onChange={(e) => setWantTrain(e.target.checked)}
                color="primary"
                inputProps={{ "aria-label": "secondary checkbox" }}
              />
              yes
              <Checkbox
                checked={!wantTrain ? true : false}
                onChange={(event) => setWantTrain(!event.target.checked)}
                color="primary"
                inputProps={{ "aria-label": "secondary checkbox" }}
              />
              No
            </div>
          </Grid>
        ) : (
          <p> </p>
        )}
        {wantTrain ? (
          <Grid item xs={12}>
            <TextField
              required
              id="num_of_apprentices"
              label="How many apprentices can u take on at a time?"
              fullWidth
              type={"number"}
              autoComplete="num_of_apprentices"
            />
          </Grid>
        ) : (
          <p> </p>
        )}
        {wantTrain ? (
          <Grid item xs={12}>
            {/*<TextField*/}
            {/*    id="outlined-textarea"*/}
            {/*    label="Multiline Placeholder"*/}
            {/*    placeholder="Placeholder"*/}
            {/*    multiline*/}
            {/*    variant="outlined"*/}
            {/*/>*/}
            <TextField
              required
              id="support_des"
              label="What additional support would you need from NBBSI to be able to train apprentices?"
              fullWidth
              multiline
              variant="outlined"
              InputLabelProps={{
                shrink: true,
              }}
              autoComplete="support_des"
            />
          </Grid>
        ) : (
          <p></p>
        )}
      </Grid>
    </React.Fragment>
  );
}
