import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Business from "./Business";
import PersonalForm from "./PersonalForm";
import TrainForm from "./TrainForm";
import { Get } from "../util/transport";
import CryptoJS from "crypto-js";
const useStyles = makeStyles((theme) => ({
  appBar: {
    position: "relative",
  },
  layout: {
    width: "auto",
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
      width: 600,
      marginLeft: "auto",
      marginRight: "auto",
    },
  },
  paper: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    padding: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
      marginTop: theme.spacing(6),
      marginBottom: theme.spacing(6),
      padding: theme.spacing(3),
    },
  },
  stepper: {
    padding: theme.spacing(3, 0, 5),
  },
  buttons: {
    display: "flex",
    justifyContent: "flex-end",
  },
  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1),
  },
}));

const steps = ["Personal Details", "Business Details", "Training Experience"];

const getTitle = (step, titles) => {
  switch (step) {
    case 0:
      return titles[0];
    case 1:
      return titles[1];
    case 2:
      return titles[2];
    default:
      throw new Error("Unknown Title");
  }
};

export default function Landing() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const [email, setemail] = useState("");
  const [name, setName] = useState("");
  const [gender, setgender] = useState("");
  const [telephone, setTelephone] = useState("");
  const [digital_address, setDigitalAddress] = useState("");
  const [business_name, setBusName] = useState("");

  const [regions, setRegions] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState(null);

    const [trainer_id, setTrainerId] = useState('')
    const [trainer_name,setTrainerName] = useState('')
    const [region,setRegion] = useState('')
    const [district,setDistrict] = useState('')
    const [town,setTown] = useState('')
    const [registered,setRegistered] = useState('')
    const [registration_no,setRegistrationNumber] =useState('')
    const [has_tin,setHasTin] = useState('')
    const [tin_no,setTinNo] = useState('')
    const [association_member,setAssociation] = useState('')
    const [nvti_cert, setNvtiCert] =useState('')
    const [rcvd_nbssi_support,setNbssiSupport ] =useState('')
    const [want_nbssi_support,setWantSupport ] =useState('')
    const [support_description,setSupportDesc ] =useState('')
    const [years_practicing, setYearsPractice] =useState('')
    const [trained_apprentice, setTrainedApprentice] =useState('')
    const [want_train_apprentice, setWantTrain] =useState('')
    const [no_apprentices, setNoApprentices] =useState('')
    const [additional_support, setAdditionalSupport] =useState('')
    const [createdon, setCreatedOn] =useState('')
    const [business_location,setBusinessLocation] =useState('')

  const getAllRegions = async () => {
    await Get("/region/wapp/regions")
      .then((res) => {
        console.log(res.data);
        const bytes = CryptoJS.AES.decrypt(res.data, "Fg7EZ/RRa0Y=").toString();
        const decrytedData = bytes.toString();
        console.log({ decrytedData });
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    getAllRegions();
  }, []);

  function getStepContent(step) {
    switch (step) {
      case 0:
        return (
          <PersonalForm
            setName={setName}
            setGender={setgender}
            setEmail={setemail}
            setTelephone={setTelephone}
            setDigitalAddress={setDigitalAddress}
          />
        );
      case 1:
        return <Business setBusName={setBusName} />;
      case 2:
        return <TrainForm />;
      default:
        throw new Error("Unknown step");
    }
  }

  const handleNext = () => {
    setActiveStep(activeStep + 1);
    // switch (activeStep) {
    // case 0:
    // !name || !email || !gender || !telephone || !digitalAddress ? console.log("Fill all form fields") :
    // break;
    // case 1:
    //     !name || !email || !gender || !telephone || !digitalAddress ? console.log("Fill all form fields") : setActiveStep(activeStep + 1);
    //     break;
    // case 2:
    //     !name || !email || !gender || !telephone || !digitalAddress ? console.log("Fill all form fields") : setActiveStep(activeStep + 1);
    //     break;
    //
    //   default:
    //     throw new Error('Unknown step');;
    // }
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  return (
    <React.Fragment>
      <CssBaseline />
      <AppBar position="absolute" color="default" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap>
            NBSSI
          </Typography>
        </Toolbar>
      </AppBar>
      <main className={classes.layout}>
        <Paper className={classes.paper}>
          <Typography component="h1" variant="h4" align="center">
            {getTitle(activeStep, steps)}
          </Typography>
          <Stepper activeStep={activeStep} className={classes.stepper}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
          <React.Fragment>
            {activeStep === steps.length ? (
              <React.Fragment>
                <Typography variant="h5" gutterBottom>
                  Thank you for your order.
                </Typography>
                <Typography variant="subtitle1">
                  Your order number is #2001539. We have emailed your order
                  confirmation, and will send you an update when your order has
                  shipped.
                </Typography>
              </React.Fragment>
            ) : (
              <React.Fragment>
                {getStepContent(activeStep)}
                <div className={classes.buttons}>
                  {activeStep !== 0 && (
                    <Button onClick={handleBack} className={classes.button}>
                      Back
                    </Button>
                  )}
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === steps.length - 1 ? "Submit" : "Next"}
                  </Button>
                </div>
              </React.Fragment>
            )}
          </React.Fragment>
        </Paper>
      </main>
    </React.Fragment>
  );
}
