import React, { useState } from "react";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import NativeSelect from "@material-ui/core/NativeSelect";
import Checkbox from "@material-ui/core/Checkbox";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-around",
  },
}));

export default function Business(props) {
  const [checkRegisted, setCheckRegistered] = useState(false);
  const [checkTin, setCheckTin] = useState(false);
  const [support, setSupport] = useState(false);
  const [nvti, setNvti] = useState(false);
  const [member, setMember] = useState(false);
  const [needsupport, setNeedSupport] = useState(false);
  const classes = useStyles();
  return (
    <React.Fragment>
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <TextField
            required
            id="business_name"
            onChange={(e) => props.setBusName(e.target.value)}
            label="Business name"
            fullWidth
            autoComplete="cc-name"
          />
        </Grid>
        <Grid item xs={12}>
          <label style={{ fontWeight: "bold" }}>Location :</label>

          <Grid item xs={12}>
            <TextField
              required
              id="town"
              name="town"
              label="Town"
              fullWidth
              onChange={(e) => props.setTown(e.target.value)}
              autoComplete="contact_number"
            />
          </Grid>
          <Grid item xs={12} className={classes.row}>
            <Grid item xs={12} md={6}>
              <NativeSelect
                className={classes.selectEmpty}
                style={{ marginRight: 5 }}
                name="age"
                onChange={(event) => props.setDistrict(event.target.value)}
                inputProps={{ "aria-label": "district" }}
                fullWidth
              >
                <option value="">District</option>
                <option value={"Akuapim East"}>Akuapim East</option>
                <option value={"Ga East"}>Ga East</option>
                <option value={"Ga West"}>Ga West</option>
              </NativeSelect>
            </Grid>

            <Grid item xs={12} md={6}>
              <NativeSelect
                className={classes.selectEmpty}
                style={{ marginLeft: 5 }}
                name="region"
                onChange={(event) => props.setDistrict(event.target.value)}
                inputProps={{ "aria-label": "region" }}
                fullWidth
              >
                <option value="">Region</option>
                <option value={"Upper East"}>Upper East</option>
                <option value={"Upper West"}>Upper West</option>
                <option value={"Ashanti"}>Ashanti</option>
                <option value={"Northern"}>Northern</option>
                <option value={"Western"}>Western</option>
                <option value={"Eastern"}>Eastern</option>
                <option value={"Greater Accra"}>Greater Accra</option>
                <option value={"Central"}>Central</option>
                <option value={"Brong"}>Brong</option>
