import React, { useState } from "react";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import NativeSelect from "@material-ui/core/NativeSelect";
import Checkbox from "@material-ui/core/Checkbox";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-around",
  },
}));

export default function Business(props) {
  const [checkRegisted, setCheckRegistered] = useState(false);
  const [checkTin, setCheckTin] = useState(false);
  const [support, setSupport] = useState(false);
  const [nvti, setNvti] = useState(false);
  const [member, setMember] = useState(false);
  const [needsupport, setNeedSupport] = useState(false);
  const classes = useStyles();
  return (
    <React.Fragment>
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <TextField
            required
            id="business_name"
            onChange={(e) => props.setBusName(e.target.value)}
            label="Business name"
            fullWidth
            autoComplete="cc-name"
          />
        </Grid>
        <Grid item xs={12}>
          <label style={{ fontWeight: "bold" }}>Location :</label>

          <Grid item xs={12}>
            <TextField
              required
              id="town"
              name="town"
              label="Town"
              fullWidth
              onChange={(e) => props.setTown(e.target.value)}
              autoComplete="contact_number"
            />
          </Grid>
          <Grid item xs={12} className={classes.row}>
            <Grid item xs={12} md={6}>
              <NativeSelect
                className={classes.selectEmpty}
                style={{ marginRight: 5 }}
                name="age"
                onChange={(event) => props.setDistrict(e.target.value)}
                inputProps={{ "aria-label": "district" }}
                fullWidth
              >
                <option value="">District</option>
                <option value={10}>Ten</option>
                <option value={20}>Twenty</option>
                <option value={30}>Thirty</option>
              </NativeSelect>
            </Grid>

            <Grid item xs={12} md={6}>
              <NativeSelect
                className={classes.selectEmpty}
                style={{ marginLeft: 5 }}
                name="region"
                inputProps={{ "aria-label": "region" }}
                fullWidth
              >
                <option value="">Region</option>
                <option value={10}>Ten</option>
                <option value={20}>Twenty</option>
                <option value={30}>Thirty</option>
              </NativeSelect>
            </Grid>
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <div style={{}}>
            <label style={{ fontWeight: "bold" }}>
              Is your business registered ?
            </label>
            <Checkbox
              checked={checkRegisted}
              onChange={(e) => setCheckRegistered(e.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            yes
            <Checkbox
              checked={!checkRegisted ? true : false}
              onChange={(event) => setCheckRegistered(!event.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            No
          </div>
        </Grid>
        {checkRegisted ? (
          <Grid item xs={12}>
            <TextField
              required
              id="tin_number"
              name="town"
              label="Business registration number"
              fullWidth
              autoComplete="contact_number"
            />
          </Grid>
        ) : (
          <p></p>
        )}

        <Grid item xs={12}>
          <div style={{}}>
            <label style={{ fontWeight: "bold" }}>
              Do you have a business Tin number?
            </label>
            <Checkbox
              checked={checkTin}
              onChange={(e) => setCheckTin(e.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            yes
            <Checkbox
              checked={!checkTin ? true : false}
              onChange={(event) => setCheckTin(!event.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            No
          </div>
        </Grid>
        {checkTin ? (
          <Grid item xs={12}>
            <TextField
              required
              id="tin_number"
              name="town"
              label="Business Tin number"
              fullWidth
              // onChange={(e)=> props.setTown(e.target.value)}
              autoComplete="contact_number"
            />
          </Grid>
        ) : (
          <p></p>
        )}

        <Grid item xs={12}>
          <div style={{}}>
            <label style={{ fontWeight: "bold" }}>
              Are you a member of a trade association in your locality?
            </label>
            <Checkbox
              checked={member}
              onChange={(e) => setMember(e.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            yes
            <Checkbox
              checked={!member ? true : false}
              onChange={(event) => setMember(!event.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            No
          </div>
        </Grid>

        <Grid item xs={12}>
          <div style={{}}>
            <label style={{ fontWeight: "bold" }}>
              Do you have NVTI certificate?
            </label>
            <Checkbox
              checked={nvti}
              onChange={(e) => setNvti(e.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            yes
            <Checkbox
              checked={!nvti ? true : false}
              onChange={(event) => setNvti(!event.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            No
          </div>
        </Grid>

        <Grid item xs={12}>
          <div style={{}}>
            <label style={{ fontWeight: "bold" }}>
              Have you received any support from NBSSI?
            </label>
            <Checkbox
              checked={support}
              onChange={(e) => setSupport(e.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            yes
            <Checkbox
              checked={!support ? true : false}
              onChange={(event) => setSupport(!event.target.checked)}
              color="primary"
              inputProps={{ "aria-label": "secondary checkbox" }}
            />
            No
          </div>
        </Grid>
        {!support ? (
          <Grid item xs={12}>
            <div style={{}}>
              <label style={{ fontWeight: "bold" }}>
                Would you want to receive support from NBSSI?
              </label>
              <Checkbox
                checked={needsupport}
                onChange={(e) => setNeedSupport(e.target.checked)}
                color="primary"
                inputProps={{ "aria-label": "secondary checkbox" }}
              />
              yes
              <Checkbox
                checked={!needsupport ? true : false}
                onChange={(event) => setNeedSupport(!event.target.checked)}
                color="primary"
                inputProps={{ "aria-label": "secondary checkbox" }}
              />
              No
            </div>
          </Grid>
        ) : (
          <p> </p>
        )}

        {needsupport && !support ? (
          <Grid item xs={12}>
            <TextField
              required
              id="support_type"
              name="support_type"
              label="What kind of support do you need from NBSSI?"
              fullWidth
              onChange={(e) => props.setSupportDesc(e.target.value)}
              autoComplete="support_type"
            />
          </Grid>
        ) : (
          <p></p>
        )}

        <Grid item xs={12}>
          <TextField
            required
            id="years_of_experiece"
            name="years_of_experiece"
            label="How many years have you been a skilled craft person?"
            fullWidth
            // onChange={(e)=> props.setTown(e.target.value)}
            autoComplete="years_of_experiece"
          />
        </Grid>
      </Grid>
    </React.Fragment>
  );
}
