import React,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Grid from '@material-ui/core/Grid';
import Checkbox from "@material-ui/core/Checkbox";


const useStyles = makeStyles((theme) => ({
  listItem: {
    padding: theme.spacing(1, 0),
  },
  total: {
    fontWeight: 700,
  },
  title: {
    marginTop: theme.spacing(2),
  },
}));

export default function TrainForm(props) {
  const [trained,setTrained] = useState(false)

  return (
    <React.Fragment>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <div style={{}}>
            <label style={{fontWeight:'bold'}}>Have you trained apprentices before?</label>
            <Checkbox
                checked={trained}
                onChange={(e)=>setTrained(e.target.checked)}
                color="primary"
                inputProps={{ 'aria-label': 'secondary checkbox' }}
            />yes

            <Checkbox
                checked={!trained ? true : false}
                onChange={event => setTrained(!event.target.checked)}
                color="primary"
                inputProps={{ 'aria-label': 'secondary checkbox' }}
            />No
          </div>
        </Grid>
        {
          !trained ?
              <Grid item xs={12}>
                <div style={{}}>
                  <label style={{fontWeight:'bold'}}>Would you want to receive support from NBSSI?</label>
                  <Checkbox
                      checked={needsupport}
                      onChange={(e)=>setNeedSupport(e.target.checked)}
                      color="primary"
                      inputProps={{ 'aria-label': 'secondary checkbox' }}
                  />yes

                  <Checkbox
                      checked={!needsupport ? true : false}
                      onChange={event => setCheckTin(!event.target.checked)}
                      color="primary"
                      inputProps={{ 'aria-label': 'secondary checkbox' }}
                  />No
                </div>
              </Grid> : <p> </p>
        }
      </Grid>
    </React.Fragment>
  );
}
